// import {Footer} from "./components/Footer";
// import {Navbar} from "./components/Navbar";
// import {Main} from "./components/Main";
import {Hero} from "././components/Hero";
import { Link } from "react-router-dom";
import "./Beats/style.css";
import { useState, createContext } from "react";
import "./App.css";
import amazonLogo from "./Beats/Photos/amazonLogo.png";
import appleLogo from "./Beats/Photos/appleLogo.png";
import beatsLogo from "./Beats/Photos/beatsLogo.png";
import blackLogo from "./Beats/Photos/black.png";
import blueLogo from "./Beats/Photos/blue.png";
import mainImage from "./Beats/Photos/mainImage.png";
import nightBlack from "./Beats/Photos/nightBlack.png";
import onEar from "./Beats/Photos/onEar.png";
import redBlack from "./Beats/Photos/redBlack.png";
import spotifyLogo from "./Beats/Photos/spotifyLogo.png";
import twilightGray from "./Beats/Photos/twilightGray.png";
import youtubeLogo from "./Beats/Photos/youtubeLogo.png";



import facebook from "./Beats/Icons/facebook.png";
import instagram from "./Beats/Icons/instagram.png";
import linkedin from "./Beats/Icons/linkedin.png";
import productLogo from "./Beats/Icons/productLogo.png";
import rightArrow from "./Beats/Icons/rightArrow.png";
import shoppingBag from "./Beats/Icons/shoppingBag.png";
import twitter from "./Beats/Icons/twitter.png";
import upArrow from "./Beats/Icons/upArrow.png";

export const myContext = createContext("");

function App({value}) {
  debugger;
  const menu = value;
  console.log(menu);
  // const handleScroll = (ref) => {
  //   window.scrollTo({
  //     top: ref.offsetTop,
  //     left: 0,
  //     behavior: "smooth",
  //   });
  // };
  return (
    <myContext.Provider value={{menu}}>
      <section className="home-section">
        <nav className="navbar">
          <div className="logo">
            <img src={amazonLogo} alt="logo" />
          </div>
          <div className="right-nav">
            <ul>
              <li>
                <a href="#">Home</a>{" "}
              </li>

              <li>
                <Link to="/Specs" onClick={()=>{  window.scrollTo(0, 500);}}>
                  <li>Specs</li>
                </Link>
              </li>
              <li>
                <Link to="/Case">
                  <li>Case</li>
                </Link>
              </li>
              <li>
                <Link to="/Products">
                  <li>Products</li>
                </Link>
              </li>
            </ul>
            <button className="nav-btn">Shop</button>
          </div>
        </nav>
        <div className="main-home">
          <div className="main-image">
            <img src={mainImage} alt="main-image" />
          </div>
          <div className="right-home">
            <img src={onEar} alt="image" id="onear-img" />
            <h1>Beats 3</h1>
            <h3>Overview</h3>
            <p>
              Enjoy award-winning Beats sound with wireless <br /> listening
              freedom and a sleek, steamlined design <br /> with comfortable
              padded earphones, delivering <br /> first-rate playback.
            </p>
            <button>
              <img src={shoppingBag} alt="bag-image" />
              Add to Bag
              <span>N399K</span>
            </button>
          </div>
        </div>

        <div className="company-logo">
          <img src={appleLogo} alt="apple-logo" />
          <img src={spotifyLogo} alt="spotify-logo" />
          <img src={amazonLogo} alt="amazon-logo" />
          <img src={youtubeLogo} alt="youtube-logo" />
        </div>
      </section>
      <Hero/>
      <footer className="footer">
        <div className="footer-logo">
          <img src={beatsLogo} alt="logo" />
        </div>
        <div className="list">
          <h3>Proucts</h3>
          <ul>
            <li>
              {" "}
              <a href>Headphones</a>{" "}
            </li>
            <li>
              {" "}
              <a href>Earphones</a>{" "}
            </li>
            <li>
              {" "}
              <a href>Earbuds</a>{" "}
            </li>
            <li>
              {" "}
              <a href>Accessories</a>{" "}
            </li>
          </ul>
        </div>
        <div className="list support">
          <h3>Support</h3>
          <ul>
            <li>
              {" "}
              <a href>Product help</a>{" "}
            </li>
            <li>
              {" "}
              <a href>Register</a>{" "}
            </li>
            <li>
              {" "}
              <a href>Updates</a>{" "}
            </li>
            <li>
              {" "}
              <a href>Provides</a>{" "}
            </li>
          </ul>
        </div>
        <div className="right-last-footer">
          <div className="user-input">
            <input type="email" name="email" placeholder="Email" />
            <a href>
              <img src={rightArrow} alt="bag-image" />
              Subscribe
            </a>
          </div>
          <div className="social-icon">
            <img src={facebook} alt="fb-icon" />
            <img src={twitter} alt="twitter-icon" />
            <img src={instagram} alt="intsagram-img" />
            <img src={linkedin} alt="linkdin-img" />
          </div>
        </div>
      </footer>
    </myContext.Provider>

    // { >
    // <div className="App">
    //   <Navbar />
    //   <Main />
    //   <Hero />
    //   <Footer />
    // </div>
    //  }
  );
}

export default App;
